import React, { Suspense } from 'react'
import { BrowserRouter, Switch, Redirect, Routes, Route,useRoutes,Router } from 'react-router-dom';
import routerList from '../config/rootRouterList'
// import Main from "../pages/main/Main";
// import Login from "../pages/login/Login";
// const Register = React.lazy(() => import('../pages/register/Register'))
// const Notfound = React.lazy(() => import('../pages/404/Notfound'))
// import RouterGuards from './RouterGuards';
const Register = React.lazy(() => import('../pages/register/Register'))
const Notfound = React.lazy(() => import('../pages/404/Notfound'))
export default function RootRouter() {
    console.log(11);
    return (
        <Suspense fallback={<h1>......加载中</h1>}>
            <BrowserRouter>
                <Routes>
                    {/* <RouterGuards></RouterGuards> */}
                    {/* <Redirect exact from='/' to='/shopping_mall'></Redirect>
                    <Route exact path='/login' component={Login}></Route>
                    <Route  path='/shopping_mall' component={Main}></Route>
                    <Route exact path='/register' component={Register}></Route>
                    <Route exact path='/404' component={Notfound}></Route>
                    <Redirect to='/404'></Redirect> */}
                    { routerList.map(item => {
                        console.log(item);
                        if(item.children){
                            return <Route key={item.path} path={item.path} element={item.element}>
                                {item.children.map(res=>(
                                    <Route key={res.path} path={res.path} element={res.element}></Route>
                                ))}
                            </Route>
                        }else{
                            return <Route key={item.path} path={item.path} element={item.element}></Route>
                        }
                    })}
                </Routes>
            </BrowserRouter>
        </Suspense>
    )
}
