import React, { Suspense } from 'react'
import { BrowserRouter, Switch, Redirect, Routes, Route,useRoutes,Router } from 'react-router-dom';
// import routerList from '../config/rootRouterList'
// import Main from "../pages/main/Main";
// import Login from "../pages/login/Login";
// const Register = React.lazy(() => import('../pages/register/Register'))
// const Notfound = React.lazy(() => import('../pages/404/Notfound'))
// import RouterGuards from './RouterGuards';
import Main from "../pages/main/Main";
import Login from "../pages/login/Login";
import Home from '../pages/home/Home';
import User from '../pages/user/User';
import Role from '../pages/role/Role';
import Commodity from '../pages/commodity/Commodity';
import Varieties from '../pages/commodity/Varieties';
import Information from '../pages/information/Information';
import TransactionFlow from '../pages/information/TransactionFlow';
import { useState } from 'react';
const Register = React.lazy(() => import('../pages/register/Register'))
const Notfound = React.lazy(() => import('../pages/404/Notfound'))
export default function RootRouter() {
    console.log(11);
    const routerList = [
        { path: 'login', element: <Login /> },
        { path: 'register', element: <Register /> },
        { path: '/', element: <Main /> },
        {
            path: 'shopping_mall', element: <Main />, children: [
                { path: 'shopping_mall/home', element: <Home /> },
                { path: 'shopping_mall/user', element: <User /> },
                { path: 'shopping_mall/role', element: <Role /> },
                { path: 'shopping_mall/commodity', element: <Commodity /> },
                { path: 'shopping_mall/varieties', element: <Varieties /> },
                { path: 'shopping_mall/information', element: <Information /> },
                { path: 'shopping_mall/transactionFlow', element: <TransactionFlow /> },
            ]
        },
        { path: '/shopping_mall', redirectTo: '/' },
        { path: '/404', element: <Notfound /> },
    ]
    return (
        <Suspense fallback={<h1>......加载中</h1>}>
            <Router>
                <Routes>
                    {/* <RouterGuards></RouterGuards> */}
                    {/* <Redirect exact from='/' to='/shopping_mall'></Redirect>
                    <Route exact path='/login' component={Login}></Route>
                    <Route  path='/shopping_mall' component={Main}></Route>
                    <Route exact path='/register' component={Register}></Route>
                    <Route exact path='/404' component={Notfound}></Route>
                    <Redirect to='/404'></Redirect> */}
                    { routerList.map(item => {
                        console.log(item);
                        if(item.children){
                            return <Route key={item.path} path={item.path} element={item.element}>
                                {item.children.map(res=>(
                                    <Route key={item.path} path={item.path} element={item.element}></Route>
                                ))}
                            </Route>
                        }else{
                            return <Route key={item.path} path={item.path} element={item.element}></Route>
                        }
                    })}
                </Routes>
            </Router>
        </Suspense>
    )
}
